import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";

const Contact = () => {
  const form = useRef();
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_n91rb9n|",  // e.g., "service_xxxx"
        "template_2c89mcs", // e.g., "template_yyyy"
        form.current,
        "Ks0DEJM-ybDEezMbK"   // e.g., "R9asdfiAbcD5"
      )
      .then(
        () => {
          setSuccess(true);
          form.current.reset();
        },
        (error) => {
          console.error(error);
          setError(true);
        }
      );
  };

  return (
    <section id="contact" className="py-20 bg-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl font-bold text-center mb-8 bg-gradient-to-tr from-pink-600 via-pink-700 to-purple-900 bg-clip-text text-transparent">
          Contact Me
        </h2>
        <form ref={form} onSubmit={sendEmail} className="max-w-2xl mx-auto space-y-6">
          <input
            type="text"
            name="user_name"
            placeholder="Your Name"
            required
            className="w-full px-4 py-2 border rounded-md"
          />
          <input
            type="email"
            name="user_email"
            placeholder="Your Email"
            required
            className="w-full px-4 py-2 border rounded-md"
          />
          <textarea
            name="message"
            rows="5"
            placeholder="Your Message"
            required
            className="w-full px-4 py-2 border rounded-md"
          />
          <button
            type="submit"
            className="bg-gradient-to-tr from-pink-600 via-pink-700 to-purple-900 text-white px-6 py-2 rounded-md hover:opacity-90"
          >
            Send Message
          </button>
        </form>

        {success && <p className="text-green-600 text-center mt-4">Message sent successfully!</p>}
        {error && <p className="text-red-600 text-center mt-4">Something went wrong. Please try again.</p>}
      </div>
    </section>
  );
};

export default Contact;
