import React, { useState } from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";
import { Github, Calendar, Tag, Info } from "lucide-react";

// Replace this with your own Dialog implementation if not using shadcn/ui
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogClose,
} from "@/components/ui/dialog";

const Projects = () => {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.1 });
  const [activeCategory, setActiveCategory] = useState("All");
  const [selectedProject, setSelectedProject] = useState(null);

  const projectCategories = ["All", "AI & Machine Learning", "Mobile App"];

  const projects = [
    {
      title: "GitHub Time Series Forecasting",
      description: "Full-stack platform for forecasting GitHub repository activity.",
      image: "/github_forecast.png",
      period: "Dec 2024",
      technologies: ["React", "Flask", "Google Cloud", "TensorFlow", "Docker"],
      detailedPoints: [
        "Built an interactive frontend using React and hosted on GCP.",
        "Implemented LSTM, Prophet, and StatsModels for forecasting GitHub star trends.",
        "Used Elasticsearch and Dockerized microservices to ensure scalable deployment.",
      ],
      github: "https://github.com/your-username/github-time-series-forecasting",
      category: "AI & Machine Learning",
    },
    {
      title: "Currency Converter App",
      description: "A sleek mobile app that converts currency in real-time using REST APIs.",
      image: "/currency_converter.png",
      period: "Oct 2024",
      technologies: ["Flutter", "Dart", "REST API"],
      detailedPoints: [
        "Developed using Flutter with MVC architecture for modular design.",
        "Integrated REST API for real-time currency rates across 3 screens.",
        "Focused on responsive UI and error handling for enhanced UX.",
      ],
      github: "https://github.com/your-username/currency-converter-app",
      category: "Mobile App",
    },
    {
      title: "Yahtzee Dice Game",
      description: "A mobile version of the classic Yahtzee game with animated interactions.",
      image: "/yahtzee_game.png",
      period: "Sep 2024",
      technologies: ["Flutter", "Dart"],
      detailedPoints: [
        "Created custom dice animations and scoring logic following official Yahtzee rules.",
        "Implemented interactive UI and persistent state management.",
        "Supported single-player mode with score tracking.",
      ],
      github: "https://github.com/your-username/yahtzee-dice-flutter",
      category: "Mobile App",
    },
    {
      title: "Flashcards Learning App",
      description: "A mobile app to create, manage, and review flashcards for quick learning.",
      image: "/flashcards.png",
      period: "Aug 2024",
      technologies: ["Flutter", "Dart"],
      detailedPoints: [
        "Users can create and organize decks, mark favorites, and toggle quiz modes.",
        "Focused on a clean, distraction-free UI with seamless navigation.",
        "Built with reusability and scalability using Dart's OOP features.",
      ],
      github: "https://github.com/your-username/flashcards-app",
      category: "Mobile App",
    },
  ];

  const filteredProjects =
    activeCategory === "All"
      ? projects
      : projects.filter((project) => project.category === activeCategory);

  const ProjectCard = ({ project, index }) => (
    <motion.div
      initial={{ opacity: 0, y: 30 }}
      animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
      transition={{ duration: 0.5, delay: 0.15 * index }}
      className="bg-white rounded-xl shadow-md overflow-hidden flex flex-col h-full"
    >
      <div className="relative h-48 w-full">
        <img
          src={project.image || "/placeholder.svg"}
          alt={project.title}
          className="object-cover w-full h-full"
        />
        <div className="absolute top-3 right-3 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full text-xs font-medium flex items-center">
          <Tag className="h-3 w-3 mr-1 text-primary" />
          {project.category}
        </div>
      </div>
      <div className="p-6 flex-1 flex flex-col">
        <div className="flex items-center text-sm text-gray-500 mb-2">
          <Calendar className="h-4 w-4 mr-1" />
          <span>{project.period}</span>
        </div>
        <h3 className="text-xl font-bold mb-2">{project.title}</h3>
        <p className="text-gray-600 mb-4">{project.description}</p>
        <div className="flex flex-wrap gap-2 mb-4">
          {project.technologies.map((tech, i) => (
            <span
              key={i}
              className="px-2 py-1 bg-gray-200 rounded-md text-xs font-medium"
            >
              {tech}
            </span>
          ))}
        </div>
        <div className="mt-auto pt-4 flex gap-3">
          <a href={project.github} target="_blank" rel="noopener noreferrer">
            <button className="border px-3 py-1 rounded flex items-center gap-2 text-sm">
              <Github className="h-4 w-4" />
              Code
            </button>
          </a>
          <button
            onClick={() => setSelectedProject(project)}
            className="border px-3 py-1 rounded flex items-center gap-2 text-sm"
          >
            <Info className="h-4 w-4" />
            Details
          </button>
        </div>
      </div>
    </motion.div>
  );

  return (
    <section id="projects" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl font-bold">Projects</h2>
          <div className="mt-2 h-1 w-20 bg-gradient-to-r from-blue-500 to-purple-500 mx-auto"></div>
          <p className="mt-4 text-gray-600 max-w-2xl mx-auto">
            Here are some of the projects I've worked on that showcase my
            technical skills and problem-solving abilities.
          </p>
        </motion.div>

        <div className="flex justify-center mb-8 flex-wrap gap-2">
          {projectCategories.map((category) => (
            <button
              key={category}
              onClick={() => setActiveCategory(category)}
              className={`px-4 py-2 rounded border ${
                activeCategory === category
                  ? "bg-blue-600 text-white"
                  : "bg-white text-gray-700"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProjects.map((project, index) => (
            <ProjectCard key={index} project={project} index={index} />
          ))}
        </div>
      </div>

      {selectedProject && (
        <Dialog open={true} onOpenChange={() => setSelectedProject(null)}>
          <DialogContent className="sm:max-w-lg">
            <DialogHeader>
              <DialogTitle>{selectedProject.title}</DialogTitle>
              <DialogDescription>{selectedProject.period}</DialogDescription>
            </DialogHeader>
            <div className="mt-4">
              <h4 className="font-medium mb-2">Technologies</h4>
              <div className="flex flex-wrap gap-2 mb-4">
                {selectedProject.technologies.map((tech, i) => (
                  <span
                    key={i}
                    className="px-2 py-1 bg-gray-200 rounded-md text-xs font-medium"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <h4 className="font-medium mb-2">Key Achievements</h4>
              <ul className="space-y-2 mb-4 text-sm text-gray-600">
                {selectedProject.detailedPoints.map((point, i) => (
                  <li key={i}>â€¢ {point}</li>
                ))}
              </ul>
              <div className="flex justify-between mt-6">
                <a
                  href={selectedProject.github}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <button className="border px-3 py-1 rounded flex items-center gap-2 text-sm">
                    <Github className="h-4 w-4" />
                    View Code
                  </button>
                </a>
                <DialogClose asChild>
                  <button
                    onClick={() => setSelectedProject(null)}
                    className="border px-3 py-1 rounded text-sm"
                  >
                    Close
                  </button>
                </DialogClose>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </section>
  );
};

export default Projects;
