import React, { useState } from "react";
import { motion } from "framer-motion";
import { Info, Github } from "lucide-react";

const projects = [
  {
    title: "GitHub Time-Series Forecasting",
    tech: ["React", "Flask", "Google Cloud", "TensorFlow", "Prophet", "StatsModels", "Docker"],
    github: "https://github.com/yourusername/github-time-series",
    description:
      "Built a full-stack app to forecast GitHub metrics using LSTM, Prophet, and StatsModels, hosted on Google Cloud with Dockerized microservices.",
  },
  {
    title: "Currency Converter",
    tech: ["Flutter", "Dart", "MVC", "REST API"],
    github: "https://github.com/yourusername/currency-converter",
    description:
      "Developed a mobile app with Flutter using OOP & MVC. Integrated real-time exchange rates via REST API with smooth multi-screen navigation.",
  },
  {
    title: "Battleships",
    tech: ["Flutter", "API", "Mobile Game", "Real-time Updates"],
    github: "https://github.com/yourusername/battleships",
    description:
      "Built a mobile game in Flutter with backend API integration and real-time active game view updates for smooth user experience.",
  },
  {
    title: "Personal Portfolio Website",
    tech: ["React", "Tailwind CSS", "Framer Motion", "JavaScript"],
    github: "https://github.com/yourusername/portfolio",
    description:
      "Created this responsive portfolio to showcase my projects and skills with animated UI using React, Tailwind, and Framer Motion.",
  },
];

export default function Projects() {
  const [activeIndex, setActiveIndex] = useState(null);

  return (
    <section id="projects" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl font-bold gradient-text">Projects</h2>
          <div className="mt-2 h-1 w-20 gradient-bg mx-auto"></div>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-2 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.4, delay: index * 0.1 }}
              className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow"
            >
              <div className="h-48 bg-gray-200 flex items-center justify-center">
                <span className="text-gray-400">Image Placeholder</span>
              </div>

              <div className="p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-xl font-bold">{project.title}</h3>
                  <button
                    onClick={() =>
                      setActiveIndex(activeIndex === index ? null : index)
                    }
                    className="text-gray-500 hover:text-primary"
                  >
                    <Info className="h-5 w-5" />
                  </button>
                </div>

                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tech.map((tech, i) => (
                    <span
                      key={i}
                      className="bg-gray-100 px-3 py-1 rounded-full text-sm"
                    >
                      {tech}
                    </span>
                  ))}
                </div>

                <a
                  href={project.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center text-sm text-blue-600 hover:underline"
                >
                  <Github className="h-4 w-4 mr-1" /> View Code
                </a>

                {activeIndex === index && (
                  <div className="mt-4 p-4 border rounded bg-gray-50 text-sm text-gray-700">
                    {project.description}
                  </div>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
