import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";
import { Calendar, MapPin } from "lucide-react";

export default function Experience() {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.1 });

  const experiences = [
    {
      title: "Backend Developer Intern",
      company: "Hyphenova",
      location: "North Hollywood, CA",
      period: "Feb 2025 – Apr 2025",
      skills: [
        "Redis", "PostgreSQL", "Kubernetes", "Docker", "AWS", "CI/CD",
        "Job Scheduling", "Distributed Systems"
      ]
    },
    {
      title: "Software Engineer 2 (Application Development Analyst)",
      company: "Accenture India Pvt. Ltd",
      location: "Pune, Maharashtra",
      period: "Mar 2023 – Aug 2023",
      skills: [
        "Java", "MySQL", "Microsoft SQL Server", "SQL Tuning", "Spring Boot",
        "Buffer Management", "Performance Optimization"
      ]
    },
    {
      title: "Software Engineer (Application Development Associate)",
      company: "Accenture India Pvt. Ltd",
      location: "Pune, Maharashtra",
      period: "Aug 2021 – Feb 2023",
      skills: [
        "Spring Boot", "JUnit", "Customer Support", "Documentation",
        "Mentorship", "Bug Fixing", "Log Analysis"
      ]
    }
  ];

  const ExperienceCard = ({ experience, index }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
      transition={{ duration: 0.5, delay: 0.1 * index }}
      className="relative pl-8 pb-8 last:pb-0 experience-timeline"
    >
      <div className="absolute left-[-8px] top-0 w-4 h-4 bg-primary rounded-full border-4 border-white"></div>
      <div className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
        <div className="flex flex-wrap justify-between items-start mb-2">
          <h3 className="text-xl font-bold">{experience.title}</h3>
          <div className="flex items-center text-sm text-gray-500 mt-1 sm:mt-0">
            <Calendar className="h-4 w-4 mr-1" />
            <span>{experience.period}</span>
          </div>
        </div>
        <div className="mb-4">
          <div className="text-primary font-medium">{experience.company}</div>
          <div className="flex items-center text-sm text-gray-500">
            <MapPin className="h-4 w-4 mr-1" />
            <span>{experience.location}</span>
          </div>
        </div>
        <div className="flex flex-wrap gap-2">
          {experience.skills.map((skill, i) => (
            <span key={i} className="px-3 py-1 skill-pill rounded-full text-sm font-medium">
              {skill}
            </span>
          ))}
        </div>
      </div>
    </motion.div>
  );

  return (
    <section id="experience" className="py-20 bg-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl font-bold gradient-text">Work Experience</h2>
          <div className="mt-2 h-1 w-20 gradient-bg mx-auto"></div>
          <p className="mt-4 text-gray-600 max-w-2xl mx-auto">
            My professional journey has equipped me with valuable experience across different roles and technologies.
          </p>
        </motion.div>

        <div className="max-w-3xl mx-auto">
          {experiences.map((experience, index) => (
            <ExperienceCard key={index} experience={experience} index={index} />
          ))}
        </div>
      </div>
    </section>
  );
}
