import React from "react";

const skillsData = [
  {
    title: "💻 Languages",
    skills: ["Java", "C", "C++", "Python", "SQL", "Dart", "Flask", "FastAPI"],
  },
  {
    title: "🌐 Web & Frameworks",
    skills: ["HTML", "CSS", "JavaScript", "React", "REST API", "Spring Boot"],
  },
  {
    title: "🧰 Tools & DevOps",
    skills: [
      "Git",
      "Docker",
      "Kubernetes",
      "Jenkins",
      "Maven",
      "Jira",
      "VS Code",
      "JUnit",
      "Selenium",
    ],
  },
  {
    title: "📊 Cloud & Analytics",
    skills: [
      "Google Cloud",
      "AWS",
      "Kafka",
      "Power BI",
      "Tableau",
      "Matplotlib",
      "Pandas",
      "Excel",
      "NetworkX",
    ],
  },
  {
    title: "🗄️ Databases",
    skills: ["SQL Server", "PostgreSQL", "NoSQL"],
  },
];

const SkillsSection = () => {
  return (
    <section id="skills" className="bg-yellow-50 py-16 px-4 text-center">
      <h2 className="text-3xl font-bold mb-4">💼 Skills & Expertise</h2>
      <p className="max-w-2xl mx-auto text-gray-600 mb-10">
        My technical toolkit includes a diverse range of programming languages,
        frameworks, and tools that I've mastered throughout my academic and
        professional journey.
      </p>

      <div className="flex flex-wrap justify-center gap-6 max-w-6xl mx-auto">
        {skillsData.map((category, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow-md p-6 w-72 hover:-translate-y-1 transition-transform"
          >
            <h3 className="text-lg font-semibold text-green-600 mb-4">
              {category.title}
            </h3>
            <div className="flex flex-wrap gap-2">
              {category.skills.map((skill, i) => (
                <span
                  key={i}
                  className="bg-teal-100 text-teal-700 px-3 py-1 rounded-full text-sm"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default SkillsSection;
