import React from "react";

const Hero = () => {
  return (
    <section id="hero" className="text-center">
      <h1 className="text-4xl font-bold">Hi, I'm Pankaj Jagtap</h1>
      <p className="mt-2 text-lg">Software Development Engineer</p>
      <div className="mt-4 space-x-4">
        <a href="#projects" className="bg-white text-green-600 border border-green-600 px-4 py-2 rounded hover:bg-green-600 hover:text-white transition">
          View Projects
        </a>
        <a href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer"
           className="bg-white text-green-600 border border-green-600 px-4 py-2 rounded hover:bg-green-600 hover:text-white transition">
          GitHub
        </a>
        <a href="https://linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer"
           className="bg-white text-green-600 border border-green-600 px-4 py-2 rounded hover:bg-green-600 hover:text-white transition">
          LinkedIn
        </a>
      </div>
    </section>
  );
};

export default Hero;
